package main

import "testing"

func TestGetStartOfPacketMarkerIndex(t *testing.T) {
	tt := []struct {
		name     string
		input    string
		expected int
	}{
		{
			name:     "one",
			input:    "bvwbjplbgvbhsrlpgdmjqwftvncz",
			expected: 23,
		},
		{
			name:     "two",
			input:    "nppdvjthqldpwncqszvftbrmjlhg",
			expected: 23,
		},
		{
			name:     "three",
			input:    "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
			expected: 29,
		},
		{
			name:     "four",
			input:    "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",
			expected: 26,
		},
		{
			name:     "five",
			input:    "mjqjpqmgbljsphdztnvjfqwrcgsmlb",
			expected: 19,
		},
		{
			name:     "final boss",
			input:    "",
			expected: 3708,
		},
	}

	for _, tc := range tt {
		t.Run(tc.name, func(t *testing.T) {
			index := getStartOfPacketMarkerIndex(tc.input, 14)

			if index != tc.expected {
				t.Errorf("expected %d; got %d", tc.expected, index)
			}
		})
	}
}
